package StackQueue;

import org.checkerframework.checker.units.qual.A;

public class App {
    public String getGreeting() {
        return "Hello World!";
    }

    public static void main(String[] args) {

        System.out.println(new App().validateBrackets("{}{Code}[Fellows](()"));


    }

    public  Boolean validateBrackets(String code){
        StackLinkedList holdOpenings = new StackLinkedList() ;
        Character c = 'c' ;
        for(int i = 0 ; i < code.length() ; i++){
            c = code.charAt(i);
            if(c=='(' || c=='[' || c=='{')
                holdOpenings.push(c);
            else {
                if (c == ')' || c == ']' || c == '}') {
                    if (holdOpenings.peek() == (Character) '(') {
                        if (c == ')') {
                            holdOpenings.pop();
                            continue;
                        }
                    }
                    if (holdOpenings.peek() == (Character) '[') {
                        if (c == ']') {
                            holdOpenings.pop();
                            continue;
                        }
                    }
                    if (holdOpenings.peek() == (Character) '{') {
                        if (c == '}') {
                            holdOpenings.pop();
                            continue;
                        }
                    }
                    return false;
                }
            }

        }

        return holdOpenings.isEmpty() ;
    }
}
